import React, { useState } from "react";
import axios from "axios";

export default function Home() {
  const [date, setDate] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [slots, setSlots] = useState([]);
  const [link, setLink] = useState(null);

  const saveSlot = async () => {
    try {
      const res = await axios.post("http://localhost:4000/api/availability", {
        userId: 1,
        date,
        startTime,
        endTime,
      });
      setSlots([...slots, res.data]);
      setDate("");
      setStartTime("");
      setEndTime("");
    } catch (error) {
      alert("Error saving availability");
    }
  };

  const generateLink = async () => {
    try {
      const res = await axios.post("http://localhost:4000/api/generate-link", {
        userId: 1,
      });
      setLink(res.data.link);
    } catch (error) {
      alert("Error generating link");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>User Availability</h2>
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} />
      <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} />
      <button onClick={saveSlot}>Save</button>

      <h3>Saved Slots (Temporary)</h3>
      <ul>
        {slots.map((s, i) => (
          <li key={i}>{s.date} | {s.startTime} - {s.endTime}</li>
        ))}
      </ul>

      <button onClick={generateLink}>Generate Link</button>
      {link && (
        <p>
          Booking Link: <a href={link} target="_blank" rel="noreferrer">{window.location.origin}{link}</a>
        </p>
      )}
    </div>
  );
}
